let getHtml = (text, color) => `
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1, maximum-scale=1, user-scalable=0">

  <title>跑马灯</title>
  <style>
    .wrapper{
      overflow: hidden;
      white-space: nowrap;
      width: 100%;
    }
    .content{
      font-size:16px;
      color:${color};
      display: inline-block;
    }

    @media (prefers-color-scheme: dark) {
    body {
    background-color: #1B1B1B;
     }
     .text,.scroll{
       color:#Fff;
       }
    }

    @media (prefers-color-scheme: light) {
    body {
    background-color: transparent;
      }
    }

  </style>
</head>
<body>

</body>
</html>`;

$.exports.show = function(arr, text, color, url, content) {
    var html = getHtml(text || '这是跑马灯默认的文字，可以自行替换。', color || 'red');

    arr.push({
        col_type: "x5_webview_single",
        url: "about:blank",
        desc: "300&&list",
        extra: {
            js: $.toString((h, url) => {
                var html = document.documentElement;
                html.innerHTML = h;

                /*const cont = document.getElementById('wrapper');
                cont.onclick = function() {
                    window.location.href = url || "about:blank/notice";
                }*/

                // 创建一个 div 元素
                var dynamicDiv = document.createElement('div');
                // 设置 div 的样式，使其显示在页面底部
                //dynamicDiv.style.position = 'fixed';
                dynamicDiv.style.bottom = '10px'; // 调整这个值以改变距离底部的距离
                dynamicDiv.style.left = '10px';
                dynamicDiv.style.padding = '10px';
                dynamicDiv.style.background = '#f0f0f0';
                dynamicDiv.style.border = '1px solid #ccc';
                // 将 div 添加到页面中
                document.body.appendChild(dynamicDiv);

                // 更新 div 的内容为当前窗口宽度
                dynamicDiv.textContent = '窗口window.innerWidth为：' + window.innerWidth + 'px';
                // 监听窗口大小变化事件，实时更新 div 的内容
                window.addEventListener('resize', function() {
                    dynamicDiv.textContent = '窗口宽度为：' + window.innerWidth + 'px';
                });


            }, html, url),
            urlInterceptor: $.toString((content) => {
                if (input === "about:blank/notice") {
                    return $.toString((content) => {
                        fba.open(JSON.stringify({
                            title: "公告",
                            url: 'hiker://empty#noRecordHistory##noHistory#',
                            findRule: "js:" + $$$.toString((content) => setResult([{
                                title: content,
                                col_type: "rich_text",
                            }]), content)
                        }));
                    }, content);
                }
            }, content || text),
        }
    });
}

